import React, {useState} from 'react';
import styled from 'styled-components';
import { Input , Button} from 'antd';
const axios = require('axios').default;

const RightContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  align-items: flex-end;
  width: 50%;
`;

const ChatWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-end;
  height: 700px;
  width: 500px;
  border-radius: 20px 20px 0 0;
  border: 30px solid #6681d6;
  border-bottom: 0;
  input {
  width: 426px !important;
  padding: 10px;
  border: 1px solid lightgrey;
  }
`;

const DefaultComandsWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  max-height: 150px;
  /* background: pink; */
  padding: 5px;
  overflow-y: auto;
`;

const DefaultComandWrap = styled.div`
  font-family: 'Roboto';
  color: white;
  border-radius: 5px;
  background: #6681d6;
  margin: 5px;
  padding: 4px 15px;
  cursor: pointer;
`;

const InputWrapper = styled.div`
display: flex;

button {
  background: lightgrey;
    border: 0;
    border-radius: 2px;
}
`;

const statusComanda = {
  IN_PROCESARE: 'IN_PROCESARE',
  CONFIRMATA: 'CONFIRMATA',
  PRELUATA_DE_CURIER: 'PRELUATA_DE_CURIER',
  IN_TRANZIT: 'IN_TRANZIT',
  LIVRATA: 'LIVRATA'
};

const data = {
  status_coamnda: {
    response: 'Buna ziua, introduceti numarul comenzii.',
    numar_comanda: [
      {
        numar_comanda: '123',
        status: statusComanda.IN_PROCESARE
      },
      {
        numar_comanda: '1234',
        status: statusComanda.IN_TRANZIT
      },
      {
        numar_comanda: '12345',
        status: statusComanda.PRELUATA_DE_CURIER
      },
      {
        numar_comanda: '123456',
        status: statusComanda.CONFIRMATA
      },
      {
        numar_comanda: '123456',
        status: statusComanda.LIVRATA
      }
    ]
  },
  returnare_comanda: {
    response: ''
  }
};

const RightContent = ({inputValue, setInputValue, infoTranspComanda, data, setData}) => {
  const [detaliiComanda, setDetaliiComanda] = useState(false);
  const onSearch = (value) => console.log(value) 
  console.log(inputValue, "ssss")
  console.log(data)
  const activeRequest=() => {
    if(detaliiComanda===true) {
      axios.post('/infoTranspComanda', {
        body: inputValue
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
  }
  return (
    <RightContentWrapper>
      <ChatWrapper>
        <div>
          <DefaultComandsWrapper>
            <DefaultComandWrap>Status comandă</DefaultComandWrap>
            <DefaultComandWrap onClick={()=>setDetaliiComanda(true)}>Detalii comandă</DefaultComandWrap>
            <DefaultComandWrap>Detalii transport</DefaultComandWrap>
            <DefaultComandWrap>Returnare comandă</DefaultComandWrap>
            <DefaultComandWrap>Ghid de mărimi</DefaultComandWrap>
            <DefaultComandWrap>Alte intrebări</DefaultComandWrap>
          </DefaultComandsWrapper>
          <div></div>
        </div>
        {/* <Search
          //placeholder="input search text"
          //onSearch={onSearch}
          //style={{ width: '500px' }}
        /> */}
        <InputWrapper>
        <Input placeholder="Introduceți mesajul dumneavoastră..." onChange={(e) => setInputValue(e.target.value)} value={inputValue} />
        <Button onClick={()=>{activeRequest(); setData([...data, inputValue]); setInputValue(null)}}>Trimite</Button>
        </InputWrapper>
      </ChatWrapper>
    </RightContentWrapper>
  );
};

export default RightContent;
